const Movie = (movie) => {
  return (
    <div key={movie.id}>
      <h2>
        {movie.title}
        {movie.original_language === "en" ? null : (
          <small> ({movie.original_title})</small>
        )}
      </h2>
      <img src={movie.poster_path} width="40%" />
      <h4>Released in {movie.release_date}</h4>
      <ul style={{ listStyleType: "none", display: "inline" }}>
        <strong style={{ float: "left" }}>Genre : </strong>{" "}
        {movie.genre_ids?.map((id) => {
          return <li style={{ float: "left" }}> &nbsp;{returnGenre(id)}</li>;
        })}
      </ul>
      <p>
        <strong>Summary</strong>
        <br />
        {movie.overview}
      </p>
      <hr />
    </div>
  );
};

export default Movie;
