import { useState, useEffect } from "react";

function App() {
    const [loading, setLoading ] = useState(true);
    const [coins, setCoins] = useState([]);
    const [dollars, setDollars] = useState(0);
    const [selectedCoin, setSelected] = useState();

    const onChange = (event) => {
      setDollars(event.currentTarget.value);
    }
    const onSelect = (event) => {
      setSelected(event.currentTarget.selectedIndex);
    }

  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then(response => response.json())
    .then(data => {
      setCoins(data)
      setLoading(false);  
    });
  }, [])

  return (
    <div>
      <h1>The Coins! {loading ? "" : `(${coins.length})`}</h1>

      <input 
      onChange={onChange}
      type="number" 
      placeholder="how much do you have?"
      ></input>

      {loading ? <strong>Loading...</strong> : (
        <select
          onChange={onSelect}
        >
          {coins.map(coin => (
            <option key={coin.id}>
              {coin.name} ({coin.symbol}): ${coin.quotes.USD.price} USD
            </option>
          ))}
      </select>)
      }
      <br />
      {selectedCoin ? 
      (<h4>
       You can buy {Math.round(dollars / coins[selectedCoin].quotes.USD.price)} {coins[selectedCoin.name]} coins with your money.
      </h4>) : null
      }

    </div>
  );
}

export default App;
