import {useParams} from "react-router-dom";
import { useState , useEffect } from "react";

const Detail = () => {
    const [movie, setMovie] = useState([]);
    const id = useParams();

    const getMovie = async() => {
        console.log(id["id"]);
        const response = await fetch(`https://nomad-movies.nomadcoders.workers.dev/movies?${id["id"]}`);
        const data = await response.json();
        setMovie(data.find(movie => movie.id == id["id"]));        
        console.log(data);
    }

    useEffect(() => {
        getMovie();
    }, [])
    return (
        <div>
        <h1>{movie.title}</h1>
        <img src={movie.backdrop_path} width="80%" />
        <h3>Rating : {"⭐️".repeat(Math.round(movie.vote_average / 2))}</h3>
        </div>
);    
}



export default Detail;
