import {useParams} from "react-router-dom";
import { useState , useEffect } from "react";

const Detail = () => {
    const [loading, setLoading] = useState(true);
    const [movie, setMovie] = useState([]);
    const id = useParams();

    const getMovie = async() => {
        console.log(id["id"]);
        const response = await fetch(`https://nomad-movies.nomadcoders.workers.dev/movies?${id["id"]}`);
        const data = await response.json();
        setMovie(data.find(movie => movie.id == id["id"]));  
        setLoading(false);      
        console.log(data);
    }

    useEffect(() => {
        getMovie();
    }, [])

    if (loading) return <h1>Loading...</h1>;

    return (
        <div className="container detail">
        <section>
        <h1 className="detail-title">{movie.title}</h1>
        <img className="detail-backdrop" src={movie.backdrop_path} width="80%" />
        <div className="detail-sub">
            <span className="rating">{"⭐️".repeat(Math.round(movie.vote_average / 2))}</span>
            <h3>{movie.adult ? <span className="badge">Adult</span> : null}</h3>
        </div>
                </section>

        </div>
);    
}



export default Detail;
