import { useState, useEffect } from "react";

function App() {
  const [counter, setValue] = useState(0);
  const [keyword, setKeyword] = useState("");

  const onClick = () => setValue(prev => prev + 1);
  const onChange = (event) => setKeyword(event.target.value);

  // telling react to execute this code when 'keyword' changes.
  // by the additional if, can add conditions
  // dependency list -> the thing that react should watch

  // useEffect is a defense against how REact works, which is refreshing the ui every time the state changes.
  useEffect(() => {
    if (keyword !== "" && keyword.length >= 5) {
      console.log("SEARCH FOR", keyword);
    }
  }, [keyword])

  useEffect(() => {
    console.log('I run when the counter changes');
  }, [counter])

  // no dependencies -> the code only runs on the first render
  // (after render finishes)
  useEffect(()=>{
    console.log("I run only once")
  }, []);


  useEffect(()=>{
    console.log("I run when keyword & counter change")
  }, [keyword, counter]);

  return (
    <div>
      <input 
      value={keyword}
      onChange={onChange} type="text" placeholder="search here.." />
      <h1>{counter}</h1>
      <button onClick={onClick}>click me</button>
    </div>
  );
}

export default App;
