import { useState, useEffect } from "react";

function Hello() {
  function effectFn(){
    console.log("Created :)")
  }useEffect( () => {
    // only logged when Hello is shown
    effectFn();
    // react js also allows you to run some code when component is destroyed.
    // a cleanup function!
    return () => console.log("destroyed :(");
  }, []);

  return <h1>Hello</h1>;
}

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => setShowing(prev => !prev);
  return (
    <div>
      {showing ? <Hello /> : null}
      <button onClick = {onClick}>{showing ? "Hide" : "Show"}</button>
    </div>
  );
}

export default App;
